import React, { useState } from 'react';
import './style.scss';
import Movie from '../../../components/Movie';
import MovieViewDetailPage from '../../Movie/ViewDetails'
import MovieDetail from '../../../components/MovieDetail';
import DatePicker from "react-datepicker";
import "react-datepicker/dist/react-datepicker.css";

import ShowTime from '../../../components/ShowTime';
import ShowtimeSpot from '../../../components/ShowtimeSpot';
export default function () {

    const [selectedMovieIndex, setSelectedMovieIndex] = useState(undefined)
    const [selectedShowtime, setSelectedShowtime] = useState(undefined)
    const [selectedShowtimeSpot, setSelectedShowtimeSpot] = useState(undefined)

    const [message, setMessage] = useState("Please select a movie")
    const [startDate, setStartDate] = useState(new Date());

    const [currentStep, setCurrentStep] = useState(2)

    const showtimes = [
        {
            date: "Tue May 23 2023 16:35:45 GMT+0700",
            spots: ["10-12", "12-14", "16-18", "20-22"],
        },
        {
            date: "Fri May 27 2023 14:20:00 GMT+0700",
            spots: ["10-12", "12-14", "16-18", "18-20"],
        },
        {
            date: "Sat May 28 2023 18:45:30 GMT+0700",
            spots: ["12-14", "14-16", "16-18", "18-20", "20-22"],
        },
        {
            date: "Wed Jun 1 2023 11:10:15 GMT+0700",
            spots: ["10-12", "14-16", "18-20"],
        },
        {
            date: "Thu Jun 2 2023 17:55:20 GMT+0700",
            spots: ["10-12", "12-14", "16-18"],
        },
        {
            date: "Sun Jun 5 2023 20:30:00 GMT+0700",
            spots: ["10-12", "14-16"],
        },
        {
            date: "Mon Jun 6 2023 12:15:45 GMT+0700",
            spots: ["10-12", "14-16", "18-20"],
        },
        {
            date: "Sat Jun 11 2023 15:00:00 GMT+0700",
            spots: ["10-12"],
        },
        {
            date: "Wed Jun 15 2023 19:30:00 GMT+0700",
            spots: ["12-14", "18-20", "20-22"],
        },
        {
            date: "Fri Jun 17 2023 10:00:00 GMT+0700",
            spots: ["10-12", "12-14", "16-18"],
        },
        {
            date: "Sun Jul 3 2023 14:20:00 GMT+0700",
            spots: ["10-12", "12-14", "16-18", "18-20"],
        },
        {
            date: "Mon Jul 4 2023 18:45:30 GMT+0700",
            spots: ["12-14", "14-16", "16-18", "18-20", "20-22"],
        },
        {
            date: "Wed Jul 6 2023 11:10:15 GMT+0700",
            spots: ["10-12", "14-16", "18-20"],
        },
        {
            date: "Fri Jul 8 2023 17:55:20 GMT+0700",
            spots: ["10-12", "12-14", "16-18"],
        },
        {
            date: "Sun Jul 10 2023 20:30:00 GMT+0700",
            spots: ["10-12", "14-16"],
        },
        {
            date: "Wed Jul 13 2023 12:15:45 GMT+0700",
            spots: ["10-12", "14-16", "18-20"],
        }
    ]

    const movies = [
        "The Power of the Dog",
        "The Assassination of Jesse James by the Coward Robert Ford",
        "Pirates of the Caribbean: The Curse of the Black Pearl",
        "Harry Potter and the Chamber of Secrets",
        "Zootopia",
        "The Silence of the Lambs",
        "Coco",
        "12 Years a Slave",
        "Catch Me If You Can",
        "The Fault in Our Stars"
    ];

    const posterLinks = [
        'https://th.bing.com/th/id/OIP.dO2JvwZyumycPLEDuiGUOwHaLa?w=186&h=287&c=7&r=0&o=5&dpr=1.3&pid=1.7',
        'https://th.bing.com/th/id/OIP.b47IsW8DKEjGyZANQnPk4AHaLH?w=123&h=184&c=7&r=0&o=5&dpr=1.5&pid=1.7',
        'https://th.bing.com/th/id/OIP.KYgpF2sELb9SVYiFPn002AHaLH?w=132&h=198&c=7&r=0&o=5&dpr=1.5&pid=1.7',
        'https://th.bing.com/th/id/OIP.FSgtqh5V3oPLxqXPHwiKcQHaLH?w=115&h=180&c=7&r=0&o=5&dpr=1.5&pid=1.7',
        'https://th.bing.com/th/id/OIP.3B6_wiyBUDlEQ2LcwKkDkAHaJ4?w=140&h=186&c=7&r=0&o=5&dpr=1.3&pid=1.7',
        'data:image/jpeg;base64,',
        "https://th.bing.com/th/id/OIP.rL0boAfmfGRN5k5yeiAhUQHaLH?w=119&h=180&c=7&r=0&o=5&dpr=1.3&pid=1.7",
        'data:image/jpeg;base64,',
        "data:image/jpeg;base64,",
        'https://th.bing.com/th/id/OIP.PifilpbXUx7NxD3h8PofUwHaLH?w=115&h=180&c=7&r=0&o=5&dpr=1.5&pid=1.7'
    ]

    const releaseDate = [
        'April 1987',
        'September 1989',
        'July 1996',
        'October 1992',
        'May 2001',
        'December 2004',
        'November 2012',
        'June 2008',
        'August 2011',
        'March 2016'
    ];

    const handlePrevStep = () => {
        if (currentStep > 1) {
            setCurrentStep(pre => pre - 1)
        }
    }

    const handleNextStep = () => {

        if (currentStep < 5) {
            // set the message for next step
            if (currentStep === 1) {
                setMessage('Please select a showtime')
            }
            setCurrentStep(pre => pre + 1)
        }


    }

    const checkIfMessageIsShown = () => {
        if (currentStep === 1) {
            return selectedMovieIndex == undefined
        }
    }

    const checkIfClickable = () => {
        if (currentStep == 1) {
            return selectedMovieIndex !== undefined;
        }
    }

    const checkIfStepActive = (stepNum) => {
        return currentStep >= stepNum;
    }

    const checkIMovieActive = (index) => {
        return selectedMovieIndex === index
    }

    const handleClickMovie = (index) => {
        if (selectedMovieIndex !== index) {
            setSelectedMovieIndex(index);
        } else {
            setSelectedMovieIndex(undefined);
        }
    }

    const handleClickShowtimeDate = (showtime) => {
        //selectedShowtime can be undefined in the beginning
        if (selectedShowtime?.date !== showtime.date) {
            setSelectedShowtime(showtime);

            // when choosing the new showtime, the old selected showtime spot can be unavailable
            // if so, reset it
            if (selectedShowtimeSpot) {
                if (!showtime.spots.includes(selectedShowtimeSpot)) {
                    setSelectedShowtimeSpot(undefined);
                }
            }
        } else {
            setSelectedShowtime(undefined);
            console.log('unchecled');
        }
    }

    const handleClickShowtimeSpot = (spot) => {
        if (selectedShowtimeSpot !== spot) {
            setSelectedShowtimeSpot(spot);
        } else {
            setSelectedShowtimeSpot(undefined);
        }
    }
    const checkIfBlur = (index) => {
        if (selectedMovieIndex !== undefined && selectedMovieIndex !== index) {
            return true;
        } else {
            return false;
        }
    }


    return (
        <div className='OrderCreatePage'>
            <div className="step-line">
                <div className="line"></div>
                <div className="step-item-container">
                    <div className={checkIfStepActive(1) ? "step-item step-item-active" : 'step-item'}>
                        <div className="step-item-number">1</div>
                        <div className="step-item-text">Select Movie</div>
                    </div>
                    <div className={checkIfStepActive(2) ? "step-item step-item-active" : 'step-item'}>
                        <div className="step-item-number">2</div>
                        <div className="step-item-text">Select Showtime</div>
                    </div>
                    <div className={checkIfStepActive(3) ? "step-item step-item-active" : 'step-item'}>
                        <div className="step-item-number">3</div>
                        <div className="step-item-text">Select seat</div>
                    </div>
                    <div className={checkIfStepActive(4) ? "step-item step-item-active" : 'step-item'}>
                        <div className="step-item-number">4</div>
                        <div className="step-item-text">Select food and drink</div>
                    </div>
                    <div className={checkIfStepActive(5) ? "step-item step-item-active" : 'step-item'}>
                        <div className="step-item-number">5</div>
                        <div className="step-item-text">Confirm</div>
                    </div>
                </div>

            </div>
            <div className="btn-container">

                <div className="btn">
                    {currentStep > 1
                        &&
                        <>
                            <i class="fa-solid fa-chevron-left" onClick={handlePrevStep}></i>
                            <p className="btn-text">Back</p>
                        </>

                    }
                </div>
                <div className="message">
                    {checkIfMessageIsShown()
                        &&
                        <p>{message}</p>
                    }
                </div>
                <div className="btn">
                    {currentStep > 0 && checkIfClickable()
                        &&
                        <>
                            <i class="fa-solid fa-chevron-right" onClick={handleNextStep}></i>
                            <p className="btn-text">Next</p>
                        </>

                    }
                </div>
            </div>
            {currentStep == 1 &&
                <div className="step-1">
                    <div className="movie-container">
                        {movies.map((title, index) => {
                            return (
                                <Movie
                                    posterLink={posterLinks[index]}
                                    movieTitle={movies[index]}
                                    releaseDate={releaseDate[index]}
                                    isActive={checkIMovieActive(index)}
                                    onClick={() => handleClickMovie(index)}
                                    blur={checkIfBlur(index)}
                                />
                            )
                        })}

                    </div>
                </div>

            }
            {currentStep == 2 &&
                <div className="step-2">
                    <div className="movie-detail">
                        <MovieDetail
                            displayLargePoster={false}
                        />
                        <div className="ShowtimeList">
                            <h1 className="showtime-list-text">Showtimes: </h1>
                            <div className="showtime-container">
                                {showtimes.map((showtime, index) => {
                                    return (
                                        <ShowTime
                                            showtime={showtime}
                                            onClick={() => handleClickShowtimeDate(showtime)}
                                            isActive={selectedShowtime && selectedShowtime.date == showtime.date}
                                        />
                                    )

                                })}
                            </div>

                        </div>

                        <div className="SpotList">
                            <h1 className="spot-list-text">Spots: </h1>
                            {selectedShowtime
                                &&
                                <div className="spot-container">
                                    {selectedShowtime.spots.map((spot, index) => {
                                        return (
                                            <ShowtimeSpot
                                                onClick={() => handleClickShowtimeSpot(spot)}
                                                spot={spot}
                                                isActive={selectedShowtime.spots.includes(spot) && selectedShowtimeSpot == spot}
                                            />
                                        )
                                    })}
                                </div>

                            }

                        </div>
                    </div>
                </div>
            }
        </div>
    )
}